example.code:
Target Restore => _ => _
        .DependsOn(Clean)
        .Executes(() => NuGetRestore(s => DefaultSettings.NuGetRestore));

Target Compile => _ => _
        .DependsOn(Restore)
        .Executes(() => MSBuild(s => DefaultSettings.MSBuildCompile));

Target Pack => _ => _
        .DependsOn(Compile)
        .Executes(() => MSBuild(s => DefaultSettings.MSBuildPack));

string relativePath = (RelativePath) "foo" / "bar";
// On Windows:   relativePath == "foo\bar"
// On Unix:      relativePath == "foo/bar"

class DefaultBuild : NukeBuild
{
    public static int Main () => Execute<DefaultBuild>(x => x.Compile);

    Target Clean => _ => _
            .Executes(() => EnsureCleanDirectory(OutputDirectory));

    Target Compile => _ => _
            .DependsOn(Clean)
            .Executes(() => MSBuild(SolutionFile);
}

[Parameter("MyGet API key for private feed."] string MyGetApiKey;
Target Push => _ => _
        .Requires(() => MyGetApiKey)
        .Executes() => { /* ... */ });

